mapper.select:
SELECT A.menu_id,A.menu_name,A.menulevcod,A.supmenucode,A.menuurl,A.menuico,A.sort
	FROM system_menu A
WHERE A.menu_id=#{menu_id}

SELECT B.menu_id,B.menu_name,B.menulevcod,B.supmenucode,B.menuurl,B.menuico
	FROM system_role_menu A,system_menu B
WHERE A.menu_id=B.menu_id
	AND A.role_id=#{role_id}
	AND B.menulevcod=#{level}
ORDER BY B.menulevcod,B.sort

SELECT B.menu_id,B.menu_name,B.menulevcod,B.supmenucode,B.menuurl,B.menuico
	FROM system_role_menu A,system_menu B
WHERE A.menu_id=B.menu_id
	AND A.role_id=#{role_id}
	AND B.supmenucode=#{supmenucode}
ORDER BY B.menulevcod,B.sort
