mapper.select:
select a.user_id, a.`key`, a.value, a.create_time, a.lastmodify_time
	from system_user_config a
where a.user_id=#{user_id}
	and a.key=#{key}

select a.user_id, a.`key`, a.value, a.create_time, a.lastmodify_time
	from system_user_config a
where a.user_id=#{user_id}

mapper.delete:
delete system_user_config system_user_config a 
where a.user_id=#{user_id}
	and a.key=#{key}
